import React from 'react'
import styled from 'styled-components'


//write color in hex
//traffic-light color
// const whole = 333;
// //actual traffic-lights
// const color1 = FF0000 //red
// const color2 = FFFF00 //yellow
// const color3 = 00FF00 //green

const TrafficLightmain = styled.div`
    position: relative;
    display: block;
    left: 50%;
    top: 50%; 
    // margin-left: -60px;
    // margin-top: -160px;
    background-color: #333333;
    width:120px;
    height:320px;
    border-radius:30px;

`

const CustomLight = styled.input`
    //remove default style
    appearance: none;
    //make the traffic lights appear
    position: relative;
    left: 50%;
    width:80px;
    height:80px;
    margin-top: 20px;
    margin-left:-40px;
    background-color: grey;
    vertical-align: middle;
    border-radius: 100%;
    display: block;
`



const TrafficLight = (props) => {
    let red
    if(props.bits == "100"){
        red = '#FF0000';
    }
    else{
        red = 'darken(#FF0000,15%)'
    }
    const RedLight = styled(CustomLight)`
        background-color: ${red => "#FF0000" || "darken(#FF0000,15%)"};
    `
const YellowLight = styled(CustomLight)`
background - color: darken(#FFFF00, 15 %);
`
const GreenLight = styled(CustomLight)`
background - color: darken(#00FF00, 15 %);
`
    return (
        <TrafficLightmain>
            <RedLight type="radio" />
            <YellowLight type="radio" />
            <GreenLight type="radio" />
        </TrafficLightmain>
    )
}

export default TrafficLight
